export const TOKEN = 'TOKEN';


export function getToken() {
    const token = localStorage.getItem(TOKEN);


    if (token) return JSON.parse(token);


    return '';

}

export function storeToken(token) {
    localStorage.setItem(token, JSON.stringify(token));
}
