export const TOKEN = 'TOKEN';


export function getToken() {
    const token = localStorage.getItem(TOKEN);


    if (token) return JSON.parse(token);


    return {
        email: '',
        id: '',
        token: ''
    }
}

export function storeUser(user) {
    localStorage.setItem(TOKEN, JSON.stringify(user));
}
